select concat('UPDATE ENROLMENT SET KEY_CURRICULAR_COURSE_SCOPE = ', ccs.KEY_CURRICULAR_COURSE, ' WHERE ID_INTERNAL = ', e.ID_INTERNAL, '; -- KEY_STUDENT_CURRICULAR_PLAN = [', e.KEY_STUDENT_CURRICULAR_PLAN, '] KEY_EXECUTION_PERIOD = [', e.KEY_EXECUTION_PERIOD, '] STATE = [', e.STATE, ']') AS "" from ENROLMENT e inner join CURRICULAR_COURSE_SCOPE ccs on e.KEY_CURRICULAR_COURSE_SCOPE = ccs.ID_INTERNAL order by e.KEY_STUDENT_CURRICULAR_PLAN;
