SELECT CONCAT('UPDATE CURRICULAR_COURSE SET CURRICULAR_COURSE.KEY_COMPETENCE_COURSE = ', COMPETENCE_COURSE.ID_INTERNAL,' WHERE CURRICULAR_COURSE.ID_INTERNAL =', CURRICULAR_COURSE.ID_INTERNAL,';') AS "" FROM 
CURRICULAR_COURSE INNER JOIN DEGREE_CURRICULAR_PLAN ON CURRICULAR_COURSE.KEY_DEGREE_CURRICULAR_PLAN = DEGREE_CURRICULAR_PLAN.ID_INTERNAL INNER JOIN DEGREE ON DEGREE_CURRICULAR_PLAN.KEY_DEGREE = DEGREE.ID_INTERNAL INNER JOIN COMPETENCE_COURSE ON COMPETENCE_COURSE.CODE = CURRICULAR_COURSE.CODE AND COMPETENCE_COURSE.NAME = CURRICULAR_COURSE.NAME WHERE DEGREE.TYPE_DEGREE = "DEGREE";
