update ROLE set ROLE_TYPE = 'RESOURCE_ALLOCATION_MANAGER' where ROLE_TYPE = 'TIME_TABLE_MANAGER';
update ROLE set PORTAL_SUB_APPLICATION = '/resourceAllocationManager' where ROLE_TYPE = 'RESOURCE_ALLOCATION_MANAGER';
update ROLE set PAGE_NAME_PROPERTY = 'portal.resourceAllocationManager' where ROLE_TYPE = 'RESOURCE_ALLOCATION_MANAGER';

update AVAILABILITY_POLICY set TARGET_GROUP = "role(RESOURCE_ALLOCATION_MANAGER)", EXPRESSION = "role(RESOURCE_ALLOCATION_MANAGER)" WHERE EXPRESSION = "role(TIME_TABLE_MANAGER)";

update ACCESSIBLE_ITEM set NAME = 'pt18:Gestão de Recursos', PATH = '/resourceAllocationManager/paginaPrincipal.do', PREFIX = '/resourceAllocationManager' where ID_INTERNAL in (select KEY_ACCESSIBLE_ITEM from AVAILABILITY_POLICY where EXPRESSION = "role(RESOURCE_ALLOCATION_MANAGER)");
