SELECT CONCAT("UPDATE CONTENT  SET CONTENT.APPROVERS = \"unitSiteManagers($I(",  S.ID_INTERNAL , ", '" , SUBSTRING(S.OJB_CONCRETE_CLASS, LENGTH(S.OJB_CONCRETE_CLASS) - (LOCATE(".", REVERSE(S.OJB_CONCRETE_CLASS))) + 2 , LOCATE(".", REVERSE(S.OJB_CONCRETE_CLASS)) - 1), "'))\" WHERE ID_INTERNAL = " , AB.ID_INTERNAL, ";") AS "" FROM CONTENT AB, PARTY U, CONTENT S WHERE AB.OJB_CONCRETE_CLASS = "net.sourceforge.fenixedu.domain.messaging.UnitAnnouncementBoard" AND AB.KEY_PARTY = U.ID_INTERNAL AND U.KEY_SITE = S.ID_INTERNAL;