insert into NODE (KEY_PARENT, KEY_CHILD, OJB_CONCRETE_CLASS, NODE_ORDER) select C.ID_INTERNAL, C2.ID_INTERNAL, 'net.sourceforge.fenixedu.domain.contents.ExplicitOrderNode', (SELECT COUNT(*)+(SELECT COUNT(*) FROM CONTENT WHERE OJB_CONCRETE_CLASS like '%.MetaDomainObjectPortal' AND ID_INTERNAL<=C2.ID_INTERNAL) FROM NODE WHERE KEY_PARENT=C.ID_INTERNAL) FROM CONTENT C, CONTENT C2 WHERE C.OJB_CONCRETE_CLASS LIKE '%.Portal' AND C2.OJB_CONCRETE_CLASS LIKE '%.MetaDomainObjectPortal';
