select concat('insert into AUTHORSHIP values(null, 1, ', PUBLICATION_AUTHOR.KEY_PUBLICATION, ', ', PERSON.ID_INTERNAL, ', ', PUBLICATION_AUTHOR.AUTHOR_ORDER, ');') as "" from PUBLICATION_AUTHOR inner join AUTHOR on AUTHOR.ID_INTERNAL = PUBLICATION_AUTHOR.KEY_AUTHOR left join PERSON on PERSON.ID_INTERNAL = AUTHOR.KEY_PERSON where PERSON.ID_INTERNAL is not null;
