UPDATE ASSIDUOUSNESS_CLOSED_MONTH INNER JOIN ASSIDUOUSNESS ON ASSIDUOUSNESS_CLOSED_MONTH.KEY_ASSIDUOUSNESS=ASSIDUOUSNESS.ID_INTERNAL INNER JOIN ass_FUNCIONARIO ON ASSIDUOUSNESS.KEY_EMPLOYEE= ass_FUNCIONARIO.ID_INTERNAL INNER JOIN CLOSED_MONTH ON ASSIDUOUSNESS_CLOSED_MONTH.KEY_CLOSED_MONTH=CLOSED_MONTH.ID_INTERNAL SET BALANCE=11820000 WHERE ass_FUNCIONARIO.EMPLOYEE_NUMBER=3718 AND CLOSED_MONTH.CLOSED_YEAR_MONTH='year=2007,monthOfYear=1';
UPDATE ASSIDUOUSNESS_EXTRA_WORK INNER JOIN ASSIDUOUSNESS_CLOSED_MONTH ON ASSIDUOUSNESS_CLOSED_MONTH.ID_INTERNAL=ASSIDUOUSNESS_EXTRA_WORK.KEY_ASSIDUOUSNESS_CLOSED_MONTH INNER JOIN ASSIDUOUSNESS ON ASSIDUOUSNESS_CLOSED_MONTH.KEY_ASSIDUOUSNESS=ASSIDUOUSNESS.ID_INTERNAL INNER JOIN ass_FUNCIONARIO ON ASSIDUOUSNESS.KEY_EMPLOYEE= ass_FUNCIONARIO.ID_INTERNAL  INNER JOIN CLOSED_MONTH ON ASSIDUOUSNESS_CLOSED_MONTH.KEY_CLOSED_MONTH=CLOSED_MONTH.ID_INTERNAL SET UNJUSTIFIED=0 WHERE ass_FUNCIONARIO.EMPLOYEE_NUMBER=3718 AND CLOSED_MONTH.CLOSED_YEAR_MONTH='year=2007,monthOfYear=1';